SET NAMES UTF8;
DROP DATABASE IF EXISTS lativ;
CREATE DATABASE lativ CHARSET=UTF8;
USE lativ;
CREATE TABLE indexs(
  ii	TINYINT PRIMARY KEY AUTO_INCREMENT,
  img_url   VARCHAR(60),
  router    VARCHAR(60)
);
CREATE TABLE list(
  did  TINYINT PRIMARY KEY AUTO_INCREMENT,
  title VARCHAR(8),
  classify  VARCHAR(8),
  big      VARCHAR(64),
  img      VARCHAR(5000),
  imgshow     VARCHAR(500)
);
CREATE TABLE details(
  id  TINYINT PRIMARY KEY AUTO_INCREMENT,
  title VARCHAR(8),
  classify  VARCHAR(8),
  shows      VARCHAR(8),
  smalls     VARCHAR(64),
  big       VARCHAR(6000),
  sname      VARCHAR(64),
  price      DECIMAL(6,2),
  size       VARCHAR(120),
  color      VARCHAR(64),
  color_title  VARCHAR(8)
)




